import React, { Component } from "react";
import {
  IconButton,
  Dialog,
  Button,
  Icon,
  Grid,
  TextField,
  DialogActions,
} from "@material-ui/core";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Autocomplete from '@material-ui/lab/Autocomplete';

import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import {
  addNewEmploeyee,
  updateEmployee,
  nameWasUsed,
  getAllDepartmentTest,
} from "./Employee1Service";
import DialogContent from "@material-ui/core/DialogContent";
import Draggable from "react-draggable";
import Paper from "@material-ui/core/Paper";

toast.configure();
function PaperComponent(props) {
  return (
    <Draggable
      handle="#draggable-dialog-title"
      cancel={'[class*="MuiDialogContent-root"]'}
    >
      <Paper {...props} />
    </Draggable>
  );
}

class Employee1EditDialog extends Component {
  state = {
    userList: [],
    name: "",
    department:"",
    totalElements: 0,
    rowsPerPage: 25,
    page: 0,
    value: { text: 'Football', id: 2 }
  };

  handleChange = (event, source) => {
    event.persist();
    if (source === "switch") {
      this.setState({ isActive: event.target.checked });
      return;
    }
    this.setState({
      [event.target.name]: event.target.value,
    });
    sports = [
      { text: 'Basketball', id: 1 },
      { text: 'Football', id: 2 },
      { text: 'Tennis', id: 3 },
      { text: 'Volleyball', id: 4 }
  ];
  handleChange = (event) => {
    this.setState({
        value: event.target.value
    });
}
  };

  handleFormSubmit = () => {
    let { t } = this.props;
    let { id, name } = this.state;
    this.setState({ disabled: true });
    this.props.handleOKEditClose();
    console.log(this.state);
    if (id) {
      updateEmployee({
        ...this.state
      }).then(() => {
        this.props.handleClose();
      });
    } else {
      addNewEmploeyee({
        ...this.state
      }).then(() => {
        this.props.handleClose();
      });
    }
  };


  componentDidMount() {
    this.updatePageData();
    
  }

  updatePageData = () => {
    getAllDepartmentTest().then(({ data }) => this.setState({ userList: [...data] }));
    console.log(this.state.userList);
  };

  render() {
    let { id, code, name, disabled,department} = this.state;
    let top100Films = this.state.userList;
 
    let { open, t } = this.props;
    return (
      <Dialog
        open={open}
        PaperComponent={PaperComponent}
        maxWidth="sm"
        fullWidth="fullWidth"
      >
        <ValidatorForm ref="form" onSubmit={this.handleFormSubmit}>
          <div
            style={{ cursor: "move" }}
            id="draggable-dialog-title"
            className="flex flex-space-between flex-middle pl-16 pr-8 py-8 bg-primary"
          >
            <h4 className="m-0 text-white">
              {this.state.id
                ? t("employee.update")
                : t("employee.add")}
            </h4>
            <IconButton onClick={this.props.handleClose}>
              <Icon className="text-white">clear</Icon>
            </IconButton>
          </div>
          <DialogContent>
            <Grid className="mb-10" container spacing={3}>
              <Grid item md={6} sm={6} xs={6}>
                <TextValidator
                  className="w-100"
                  label={t("employee.name")}
                  onChange={this.handleChange}
                  type="text"
                  name="name"
                  value={name}
                  validators={["required"]}
                  errorMessages={["this field is required"]}
                />
   
              </Grid>
              <Grid item md={6} sm={6} xs={6}>
                   <Autocomplete
                   id="combo-box-demo"
                   name="department"
                  options={top100Films}
                   getOptionLabel={option => option.name}
                   renderInput={params => (
                    <TextField {...params} label="Combo box"  />
                     )}
                     onChange={(event, newValue) => {
                   console.log(JSON.stringify(newValue, null, ' '));
                    }}
                  />
                   <div className="example-config">
                    Selected Value: {JSON.stringify(this.state.value)}
                </div>
                <ComboBox
                    data={this.sports}
                    textField="text"
                    dataItemKey="id"
                    value={this.state.value}
                    onChange={this.handleChange}
                />
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <div className="flex flex-space-between flex-middle mt-36">
              <Button
                variant="contained"
                color="secondary"
                className="mr-36"
                onClick={() => this.props.handleClose()}
              >
                {t("general.cancel")}
              </Button>
              <Button
                variant="contained"
                color="primary"
                type="submit"
                disabled={disabled}
              >
                {t("general.save")}
              </Button>
            </div>
          </DialogActions>
        </ValidatorForm>
      </Dialog>
    );
  }
}

export default Employee1EditDialog;
